import React from 'react';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import DatePickers from '../components/routes/date/DatePickers';
import TextField from '@material-ui/core/TextField';

function getSteps() {
  return ['NameofCravings', 'TypeofCraving', 'DateofCraving', 'TimesofCraving', 'WhoGavetheWhim' ];
}

function getStepContent(step) {
  switch (step) {
    case 0:
      return this.NameofCravings;
    case 1:
      return this.TypeofCraving;
    case 2:
      return this.DateofCraving;
    case 3:
      return this.TimesofCraving;
    case 4:
      return this.WhoGavetheWhim;
    default:
      return 'Unknown step';
  }
}

class VerticalLinearStepper extends React.Component {
  state = {
    activeStep: 0,
  };


  NameofCravings() {
    return 
        <div className="form-group">
            <TextField
                onChange={this.handleChange}
                name='NameofCravings'
                id="NameofCravings"
                label="Name of Cravings"
                margin="normal"
                fullWidth
            />
        </div>  
  }

  TypeofCraving() {
    return 
        <div className="form-group">
            <TextField
                onChange={this.handleChange}
                name='TypeofCraving'
                id="TypeofCraving"
                label="Type of Cravings"
                margin="normal"
                fullWidth
            />
        </div>  
  }

  DateofCraving() {
    return 
        <div className="form-group">
          <span>Fecha </span>
              <DatePickers
                  onBlur={this.handleChange}
                  name='DateofCraving'
              />
        </div>  
  }

  TypeofCraving() {
    return 
        <div className="form-group">
            <TextField
                onChange={this.handleChange}
                name='TypeofCraving'
                id="TypeofCraving"
                label="Type of Cravings"
                margin="normal"
                fullWidth
            />
        </div>  
  }


  handleNext = () => {
    this.setState({
      activeStep: this.state.activeStep + 1,
    });
  };

  handleBack = () => {
    this.setState({
      activeStep: this.state.activeStep - 1,
    });
  };

  handleReset = () => {
    this.setState({
      activeStep: 0,
    });
  };

  render() {
    const steps = getSteps();
    const {activeStep} = this.state;

    return (
      <div className="w-100">
        <Stepper activeStep={activeStep} orientation="vertical">
          {steps.map((label, index) => {
            return (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
                <StepContent className="pb-3">
                  <Typography>{getStepContent(index)}</Typography>
                  <div className="mt-2">
                    <div>
                      <Button
                        disabled={activeStep === 0}
                        onClick={this.handleBack}
                        className="jr-btn"
                      >
                        Back
                      </Button>
                      <Button
                        variant="contained"
                        color="primary"
                        onClick={this.handleNext}
                        className="jr-btn"
                      >
                        {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                      </Button>
                    </div>
                  </div>
                </StepContent>
              </Step>
            );
          })}
        </Stepper>
        {activeStep === steps.length && (
          <Paper square elevation={0} className="p-2">
            <Typography>All steps completed - you&quot;re finished</Typography>
            <Button onClick={this.handleReset} className="jr-btn">
              Reset
            </Button>
          </Paper>
        )}
      </div>
    );
  }
}

export default VerticalLinearStepper;
