import React from 'react';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import DatePickers from '../components/routes/date/DatePickers';
import TextField from '@material-ui/core/TextField';

function getSteps() {
  return ['NameofCravings', 'TypeofCraving', 'DateofCraving', 'TimesofCraving', 'WhoGavetheWhim' ];
}

function getStepContent(step) {
  switch (step) {
    
    case 1:
      return this.TypeofCraving();
    case 2:
      return this.DateofCraving();
    case 3:
      return this.TimesofCraving();
    case 4:
      return this.WhoGavetheWhim();
    default:
      return 'Unknown step';
  }
}

class VerticalLinearStepper extends React.Component {
  state = {
    activeStep: 0,
  };

  handleChange (e) {
    const n = e.target.name; //en n me guardas el contenido que tiene name
    const valor = e.target.value;
    this.setState({
      [n]: valor //en n me pones el valor
    })
  };
  NameofCravings() {
    return 
    /*
        <div className="form-group">
            <TextField
                onChange={this.handleChange}
                name='NameofCravings'
                id="NameofCravings"
                label="Name of Cravings"
                margin="normal"
                fullWidth
            />
        </div>  

        */

        return <div>
      <div className="row">
        <div className="col-md-6">
          <div className="form-group">
            <TextField
              onChange={this.handleChange}
              name='NombrePadre'
              id="NameDad"
              label="Name"
              margin="normal"
              fullWidth
            />
          </div>
        </div>
        <div className="col-md-6">
          <div className="form-group">
            <TextField
              onChange={this.handleChange}
              name='ApellidosPadre'
              id="SurnameDad"
              label="Surname"
              margin="normal"
              fullWidth
            />
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-md-6">
          <div className="form-group">
            <span>Fecha Nacimiento</span>
              <DatePickers
                onBlur={this.handleChange}
                name="FechaNacimientoPadre"
              />
        </div>
        </div>
      </div>
    </div>
  }

  TypeofCraving() {
    return 
    return <div>
      <div className="row">
        <div className="col-md-6">
          <div className="form-group">
            <TextField
              onChange={this.handleChange}
              name='NombrePadre'
              id="NameDad"
              label="Name"
              margin="normal"
              fullWidth
            />
          </div>
        </div>
        <div className="col-md-6">
          <div className="form-group">
            <TextField
              onChange={this.handleChange}
              name='ApellidosPadre'
              id="SurnameDad"
              label="Surname"
              margin="normal"
              fullWidth
            />
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-md-6">
          <div className="form-group">
            <span>Fecha Nacimiento</span>
              <DatePickers
                onBlur={this.handleChange}
                name="FechaNacimientoPadre"
              />
        </div>
        </div>
      </div>
    </div>
    /*
        <div className="form-group">
            <TextField
                onChange={this.handleChange}
                name='TypeofCraving'
                id="TypeofCraving"
                label="Type of Cravings"
                margin="normal"
                fullWidth
            />
        </div>  
        */
  }

  DateofCraving() {
    return 
    return <div>
      <div className="row">
        <div className="col-md-6">
          <div className="form-group">
            <TextField
              onChange={this.handleChange}
              name='NombrePadre'
              id="NameDad"
              label="Name"
              margin="normal"
              fullWidth
            />
          </div>
        </div>
        <div className="col-md-6">
          <div className="form-group">
            <TextField
              onChange={this.handleChange}
              name='ApellidosPadre'
              id="SurnameDad"
              label="Surname"
              margin="normal"
              fullWidth
            />
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-md-6">
          <div className="form-group">
            <span>Fecha Nacimiento</span>
              <DatePickers
                onBlur={this.handleChange}
                name="FechaNacimientoPadre"
              />
        </div>
        </div>
      </div>
    </div>
    /*
        <div className="form-group">
          <span>Fecha </span>
              <DatePickers
                  onBlur={this.handleChange}
                  name='DateofCraving'
              />
        </div>  
*/
        
  }

  TypeofCraving() {
    return 
    /*
        <div className="form-group">
            <TextField
                onChange={this.handleChange}
                name='TypeofCraving'
                id="TypeofCraving"
                label="Type of Cravings"
                margin="normal"
                fullWidth
            />
        </div>  
*/
        
        return <div>
      <div className="row">
        <div className="col-md-6">
          <div className="form-group">
            <TextField
              onChange={this.handleChange}
              name='NombrePadre'
              id="NameDad"
              label="Name"
              margin="normal"
              fullWidth
            />
          </div>
        </div>
        <div className="col-md-6">
          <div className="form-group">
            <TextField
              onChange={this.handleChange}
              name='ApellidosPadre'
              id="SurnameDad"
              label="Surname"
              margin="normal"
              fullWidth
            />
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-md-6">
          <div className="form-group">
            <span>Fecha Nacimiento</span>
              <DatePickers
                onBlur={this.handleChange}
                name="FechaNacimientoPadre"
              />
        </div>
        </div>
      </div>
    </div>
  }


  handleNext = () => {
    this.setState({
      activeStep: this.state.activeStep + 1,
    });
  };

  handleBack = () => {
    this.setState({
      activeStep: this.state.activeStep - 1,
    });
  };

  handleReset = () => {
    this.setState({
      activeStep: 0,
    });
  };

  render() {
    const steps = getSteps();
    const {activeStep} = this.state;

    return (
      <div className="w-100">
        <Stepper activeStep={activeStep} orientation="vertical">
          {steps.map((label, index) => {
            return (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
                <StepContent className="pb-3">
                  <Typography>{getStepContent(index)}</Typography>
                </StepContent>
              </Step>
            );
          })}
        </Stepper>
        <div>
          {this.state.activeStep === steps.length ? (
            <div>
              <Typography className="my-2">
                All steps completed - you&quot;re finished
              </Typography>
              <Button onClick={this.handleReset}>Reset</Button>
            </div>
          ) : (
            <div>
              {this.getStepContent(activeStep)}
              <div>
                <Button
                  disabled={activeStep === 0}
                  onClick={this.handleBack}
                  className="mr-2"
                >
                  Back
                </Button>
                <Button variant="contained" color="primary" onClick={this.handleNext}>
                  {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                </Button>
              </div>
            </div>
          )}
        </div>
      </div>
    );
  }
}

export default VerticalLinearStepper;
