          <div className="form-group">
            <TextField
              onChange={this.handleChange}
              name='TypeofCraving'
              id="TypeofCraving"
              label="Type of Cravings"
              margin="normal"
              fullWidth
            />
          </div>
        </div> 
      </div>
    </div>    
  }

  DateofCraving() {
    return <div>
    <div className="row">
      <div className="col-md-6">
        <div className="form-group">
        <span>Fecha </span>
          <DatePickers
            onBlur={this.handleChange}
            name='DateofCraving'
          />
        </div>
      </div> 
