import React from 'react';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Checkbox from '@material-ui/core/Checkbox';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';
import IntlMessages from 'util/IntlMessages';
import DatePickers from '../date/DatePickers';


function getSteps() {
  return ['Account Information', 'Mom Information', 'Dad Information', 'Confirm and Finish'];
}

function getStepContent(stepIndex) {
  switch (stepIndex) {
    case 0:
      return getAccountInformation();
    case 1:
      return getMomInformation();
    case 2:
      return getDadInformation();
    case 3:
      return getConfirmation();

    default:
      return 'Uknown stepIndex';
  }
}

function getAccountInformation() {

  return <div>
    <div className="row">
      <div className="col-md-6">
        <div className="form-group">
          <TextField
            id="userName"
            label="UserName"
            margin="normal"
            fullWidth
          />
        </div>
      </div>
      <div className="col-md-6">
        <div className="form-group">
          <TextField
            id="password"
            label={<IntlMessages id="appModule.password"/>}
            type="password"
            autoComplete="current-password"
            margin="normal"
            fullWidth
          />
        </div>
      </div>
    </div>
    <div className="row">
      <div className="col-md-6">
        <div className="form-group">
          <TextField
            id="confirmPassword"
            label="Confirm Password"
            type="password"
            autoComplete="current-password"
            margin="normal"
            fullWidth
          />
        </div>
      </div>
    </div>
  </div>
}

function getMomInformation() {
  return <div>
    <div className="row">
      <div className="col-md-6">
        <div className="form-group">
          <TextField
            id="NameMom"
            label="Name"
            margin="normal"
            fullWidth
          />
        </div>
      </div>
      <div className="col-md-6">
        <div className="form-group">
          <TextField
            id="SurnameMom"
            label="Surname"
            margin="normal"
            fullWidth
          />
        </div>
      </div>
    </div>
    <div className="row">
      <div className="col-md-6">
        <div className="form-group">
          <span>Fecha Nacimiento</span>
            <DatePickers/>
        </div>
      </div>
      <div className="col-md-6">
        <div className="form-group">
          <span>Fecha Embarazo</span>
           <DatePickers/>
        </div>
      </div>
    </div>
  </div>

}

function getDadInformation() {
  return <div>
    <div className="row">
      <div className="col-md-6">
        <div className="form-group">
          <TextField
            id="NameDad"
            label="Name"
            margin="normal"
            fullWidth
          />
        </div>
      </div>
      <div className="col-md-6">
        <div className="form-group">
          <TextField
            id="SurnameDad"
            label="Surname"
            margin="normal"
            fullWidth
          />
        </div>
      </div>
    </div>
    <div className="row">
      <div className="col-md-6">
        <div className="form-group">
          <span>Fecha Nacimiento</span>
            <DatePickers/>
      </div>
      </div>
    </div>
  </div>
}

function getConfirmation() {
  return <div className="tab-pane" id="tab2-4">
    <h3 className="title text-primary">Terms and Conditions</h3>
    <p><strong>Lorem</strong> Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has
      been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type
      and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap
      into electronic typesetting, remaining essentially unchanged.</p>
    <div className="d-flex align-items-center">
      <Checkbox color="primary"/> <span>I agree with the Terms and Conditions.</span>
    </div>
  </div>
}

class FormSignUp extends React.Component {
  state = {
    activeStep: 0,
  };

  handleNext = () => {
    const {activeStep} = this.state;
    this.setState({
      activeStep: activeStep + 1,
    });
  };


  handleSubmit = () => {
   
    console.log('lo hizo, hadleSubmit');
  };


  handleBack = () => {
    const {activeStep} = this.state;
    this.setState({
      activeStep: activeStep - 1,
    });
  };

  handleReset = () => {
    this.setState({
      activeStep: 0,
    });
  };

  render() {
    const steps = getSteps();
    const {activeStep} = this.state;

    return (
      <div className="w-100">
        <Stepper activeStep={activeStep} alternativeLabel className="horizontal-stepper-linear">
          {steps.map((label, index) => {
            return (
              <Step key={label} className={`horizontal-stepper ${index === activeStep ? 'active' : ''}`}>
                <StepLabel className="stepperlabel">{label}</StepLabel>
              </Step>
            );
          })}
        </Stepper>
        <div>
          {this.state.activeStep === steps.length ? (
            <div>
              <Typography className="my-2">
                All steps completed - you&quot;re finished
              </Typography>
              {this.handleSubmit()}
              <Button onClick={this.handleReset}>Reset</Button>
            </div>
          ) : (
            <div>
              {getStepContent(activeStep)}
              <div>
                <Button
                  disabled={activeStep === 0}
                  onClick={this.handleBack}
                  className="mr-2"
                >
                  Back
                </Button>
                <Button variant="contained" color="primary" onClick={this.handleNext}>
                  {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                </Button>
              </div>
            </div>
          )}
        </div>
      </div>
    );
  }
}

export default FormSignUp;
