import React from 'react';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import DatePickers from '../components/routes/date/DatePickers';
import TextField from '@material-ui/core/TextField';




class VerticalLinearStepper extends React.Component {
  constructor(){
    super();
    this.state = {
      activeStep: 0,
    };
    
  }
 


 getSteps() {
    return ['NameofCravings', 'TypeofCraving', 'DateofCraving', 'TimesofCraving', 'WhoGavetheWhim' ];
  }

  NameofCravings() {
    return <div>
      <div className="row">
        <div className="col-md-6">
          <div className="form-group">
            <TextField
              onChange={this.handleChange}
              name='NameofCravings'
              id="NameofCravings"
              label="Name of Cravings"
              margin="normal"
              fullWidth
            />
          </div>
        </div> 
      </div>
    </div>
  }

  TypeofCraving() {
    return <div>
      <div className="row">
        <div className="col-md-6">
          <div className="form-group">
            <TextField
              onChange={this.handleChange}
              name='TypeofCraving'
              id="TypeofCraving"
              label="Type of Cravings"
              margin="normal"
              fullWidth
            />
          </div>
        </div> 
      </div>
    </div>    
  }

  DateofCraving() {
    return <div>
    <div className="row">
      <div className="col-md-6">
        <div className="form-group">
        <span>Fecha </span>
          <DatePickers
            onBlur={this.handleChange}
            name='DateofCraving'
          />
        </div>
      </div> 
    </div>
  </div>   
  }

  TimesofCraving() {
    return <div>
    <div className="row">
      <div className="col-md-6">
        <div className="form-group">
          <TextField
            onChange={this.handleChange}
            name='TimesofCraving'
            id="TimesofCraving"
            label="Times of Craving"
            margin="normal"
            fullWidth
          />
        </div>
      </div> 
    </div>
  </div>  
  }


  WhoGavetheWhim() {
    return <div>
    <div className="row">
      <div className="col-md-6">
        <div className="form-group">
          <TextField
            onChange={this.handleChange}
            name='WhoGavetheWhim'
            id="WhoGavetheWhim"
            label="Who Gave the Whim"
            margin="normal"
            fullWidth
          />
        </div>
      </div> 
    </div>
  </div>  
  }

  getStepContent(stepIndex) {
    switch (stepIndex) {
      case 0:
        return this.NameofCravings;
      case 1:
        return this.TypeofCraving;
      case 2:
        return this.DateofCraving;
      case 3:
        return this.TimesofCraving;
      case 4:
        return this.WhoGavetheWhim;
      default:
        return 'Unknown stepIndex';
    }
  }

  handleNext = () => {
    const {activeStep} = this.state;
    const nextStep = activeStep + 1;
    if (nextStep === this.getSteps().length) {
      this.handleSubmit();
    }
    this.setState({
      activeStep: nextStep,
    });
  };


  handleChange (e) {
    const n = e.target.name; //en n me guardas el contenido que tiene name
    const valor = e.target.value;
    this.setState({
      [n]: valor //en n me pones el valor
    })
  };

  handleBack = () => {
    const {activeStep} = this.state;
    this.setState({
      activeStep: activeStep - 1,
    });
  };
  
  handleReset = () => {
    this.setState({
      activeStep: 0,
    });
  };

  render() {
    const steps = this.getSteps();
    const {activeStep} = this.state;

    return (
      
      <div className="w-100">
        <Stepper activeStep={activeStep} orientation="vertical">
          {steps.map((label, index) => {
            return (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>  
                );
          })}
        </Stepper>
        <div>
          {this.state.activeStep === steps.length ? (
            <div>
              <Typography className="my-2">
                All steps completed - you&quot;re finished
              </Typography>
              <Button onClick={this.handleReset}>Reset</Button>
            </div>
          ) : (
            <div>
              {this.getStepContent(activeStep)}
              <div>
                <Button
                  disabled={activeStep === 0}
                  onClick={this.handleBack}
                  className="mr-2"
                >
                  Back
                </Button>
                <Button variant="contained" color="primary" onClick={this.handleNext}>
                  {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                </Button>
              </div>
            </div>
          )}
        </div>
      </div>
     
    );
    
  }
}

export default VerticalLinearStepper;
